"use strict";function initialize(){d3.select(".chart").selectAll("div").data(data).enter().append("div").classed("bar",!0).style("width",function(a){return x(a.value)+"%"}).text(function(a){return a.name+": "+a.value.toFixed(1)})}function update(){d3.select(".chart").selectAll("div").data(data).style("width",function(a){return x(a.value)+"%"}).text(function(a){return a.name+": "+a.value.toFixed(1)})}function addData(){data.push({name:"New",value:10+20*Math.random()}),update()}var data=[{name:"A",value:10+20*Math.random()},{name:"B",value:10+20*Math.random()},{name:"C",value:10+20*Math.random()},{name:"D",value:10+20*Math.random()},{name:"E",value:10+20*Math.random()},{name:"F",value:10+20*Math.random()}],x=d3.scaleLinear().domain([0,d3.max(data,function(a){return a.value})]).range([0,100]);document.addEventListener("DOMContentLoaded",function(a){initialize()});